import { useEffect, useState, useContext } from "react";
import Colors from "../../components/Colors/Colors";
import "./song-details.css";
import UserContext from "../../helpers/UserContext";
import axios from "axios";

const SongDetails = (props) => {
  const [trackLoading, setTrackLoading] = useState(true);
  let trackId = props.match.params.trackId;
  // const [track, setTrack] = useState();
  const user = useContext(UserContext);
  let track = [];

  const [favoriteCount, setFavoriteCount] = useState(0);

  useEffect(() => {
    fetch(
      `https://thingproxy.freeboard.io/fetch/https://api.deezer.com/track/${trackId}`
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        track.push(res);
        // setTrack(res);

        setTrackLoading(false);
      });
  }, []);

  useEffect(() => {
    const trackInfo = {
      favoritedBy: user,
      trackId: trackId,
      // trackTitle: track[0].title,
      // trackArtist: track[0].artist.name,
      // trackCover: track[0].album.cover,
      // trackAlbum: track[0].album.name,
    };
    // axios.post("/api/favoriteCount", trackInfo).then((res) => {
    //   if (res.data.success) {
    //     setFavoriteCount(res.data.favoriteCount);
    //   } else {
    //     console.error("Couldn't fetch number of favorites");
    //   }
    // });
  }, []);

  return (
    <div>
      {!trackLoading ? (
        <div id="main">
          <img
            className="album-img"
            src={track[0].album.cover_xl}
            alt={`${track[0].title} cover art`}
          />
          <div id="colors" style={{ marginLeft: "20px" }}>
            <Colors />
          </div>

          <div className="track-details">
            <h1 id="title">{track[0].title}</h1>
            <p id="artist-name">Artist: {track[0].artist.name}</p>
            <br />
            <p id="album-name">Album: {track[0].album.title}</p>
            <br />
            <p id="date">
              Released: {track[0].album.release_date.substr(0, 4)}
            </p>
            <br />
            <div id="bottom-col">
              <a
                href="https://www.deezer.com/artist/4495513"
                rel="noreferrer"
                target="_blank"
                id="view-more"
              >
                See more from {track[0].artist.name} on Deezer®
                <img
                  id="artist-pic"
                  src={track[0].artist.picture}
                  alt={`${track[0].artist.name} icon`}
                />
              </a>
              <div className="addFav">
                <button>🔥{favoriteCount}</button>
              </div>
            </div>
            <audio
              id="audioPreview"
              src={track[0].preview}
              controls
              controlsList="nodownload noplaybackrate"
            >
              Your browser does not support the audio element.
            </audio>
          </div>
        </div>
      ) : (
        <h1 className="loading">Loading...</h1>
      )}
    </div>
  );
};
export default SongDetails;
